const aiResponse = $input.item.json.output || $input.item.json.text;

let candidates;
try {
  // Try to parse direct JSON
  candidates = JSON.parse(aiResponse);
} catch (e) {
  // Extract JSON from markdown code blocks or mixed content
  const jsonMatch = aiResponse.match(/```(?:json)?\s*([\s\S]*?)```/) || 
                    aiResponse.match(/\[\s*{[\s\S]*}\s*\]/);

  if (jsonMatch) {
    candidates = JSON.parse(jsonMatch[1] || jsonMatch[0]);
  } else {
    throw new Error('Unable to parse AI response as JSON');
  }
}

// Validate and normalize structure
if (!Array.isArray(candidates)) {
  candidates = [candidates];
}

// Normalize each candidate object
const normalizedCandidates = candidates.map(c => ({
  candidate_name: c.candidate_name || c.name || c.candidateName || '',
  email: c.email || '',
  phone: c.phone || '',
  match_score: parseInt(c.match_score || c.matchScore || c.score || 0),
  reasoning: c.reasoning || c.reason || '',
}));

// Filter out candidates below threshold and sort by score
const filteredCandidates = normalizedCandidates
  .filter(c => c.match_score >= 40)
  .sort((a, b) => b.match_score - a.match_score);

return filteredCandidates.map(candidate => ({ json: candidate }));
